public class BA_8n2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b;
		int result;

		a = 8;
		b = 2;

		result = a + b;
		System.out.println(a + "+" + b + "=" + result);

		result = a - b;
		System.out.println(a + "-" + b + "=" + result);

		result = a * b;
		System.out.println(a + "*" + b + "=" + result);

		result = a / b;
		System.out.println(a + "/" + b + "=" + result);

	}

}